import React, {useState, useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {Row, Col} from 'react-bootstrap'
import Product from '../components/Product'
import axios from 'axios'
import {listProducts} from '../actions/productActions'
const HomeScreen = () => {
    const dispatch = useDispatch()
    const productList = useSelector(state => state.productList)//select the state so it can be displayed
    const {loading,error,products} = productList//what could be sent from reducer
    useEffect(() => {
        dispatch(listProducts())//fire off the action, get the action, send to the reducer
    }, [dispatch])//pass as a dependency

    //dispaly the pull out info
    return (
        <>
        <h1>Latest Products</h1>

        {loading? <h2>Loading...</h2> : error? <h3>{error}</h3> :  <Row>
            {products.map(product => (
                <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                    <Product product={product}/>
                </Col>
            ))}

        </Row>} 
        </>
    )
}

export default HomeScreen
