import React, {useState, useEffect} from 'react'
import {Row, Col} from 'react-bootstrap'
import Product from '../components/Product'
import axios from 'axios'

const HomeScreen = () => {
    const [products, setProducts ] = useState([])
    //name of the state, function to change the state
//it will run as soon as it load
    useEffect(() => {
        const fetchProducts = async () => {
            const {data} = await axios.get('/api/products')//look at host 5000
            setProducts(data)//change the state
        }
        fetchProducts()//call the function
    }, [])
    ///dependency, when this change, it will fireoff the useEffect
    return (
        <>
        <h1>Latest Products</h1>
        <Row>
            {products.map(product => (
                <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                    <Product product={product}/>
                </Col>
            ))}

        </Row>

        </>
    )
}

export default HomeScreen
